     */
    @TypedRoute.Post() // 200x faster and safer JSON.stringify()
    public async store(
        @TypedBody() input: IBbsArticle.IStore // 20,000x faster validator
    ): Promise<IBbsArticle>; 
        // do not need DTO class definition, 
        // just fine with interface
}
