export type Minimum<Value extends number | bigint> = TagBase<{
    target: Value extends number ? "number" : "bigint";
    kind: "minimum";
    value: Value;
    validate: `${Numeric<Value>} <= $input`;
    exclusive: ["minimum", "exclusiveMinimum"];
